    % Callbacks that handle component events
    methods (Access = private)

        % Code that executes after component creation
        function CT_Scanner_OpeningFcn(app, varargin)
            % Create GUIDE-style callback args - Added by Migration Tool
            [hObject, eventdata, handles] = convertToGUIDECallbackArguments(app); %#ok<ASGLU>
            movegui(app.figure1, 'onscreen');
            axes(handles.axes_new); hold on;
            handles.next_plot = int16(0);
            handles.rAlign = 200;
            handles.cAlign = 220;
            handles.original_line = gobjects(1);
            handles.new_line = gobjects(1);
            handles.legend = gobjects(1);
            handles.image_diff = gobjects(1);
            handles.newcolors = {'k', 'r'}; 
            guidata(hObject, handles);
        end

        % Button pushed function: get_phantom
        function generate_phantom(app, event)
            % Create GUIDE-style callback args - Added by Migration Tool
            [hObject, eventdata, handles] = convertToGUIDECallbackArguments(app, event); %#ok<ASGLU> 
            img = phantom('Modified Shepp-Logan', app.phantom_size.Value); 
            handles.old_input = img;
            imshow(img);
            app.axes_new.Title.String = "Generated Phantom";
            set(hObject, 'Enable', 'off');
            set(app.reset, 'Enable', 'on');
            set(app.n_transducers, 'Enable', 'on');
            set(app.angular_inc, 'Enable', 'on');
            set(app.probe_type, 'Enable', 'on');
            set(app.reconstruct, 'Enable', 'on');
            guidata(hObject, handles);
        end

        % Button pushed function: reset
        function reset_App(app, event)
            % Create GUIDE-style callback args - Added by Migration Tool
            [hObject, eventdata, handles] = convertToGUIDECallbackArguments(app, event); %#ok<ASGLU>
            
            close(gcbf);
            CT_Scanner_;
        end

        % Button pushed function: reconstruct
        function reconstruct_image(app, event)
             % Create GUIDE-style callback args - Added by Migration Tool
            [hObject, eventdata, handles] = convertToGUIDECallbackArguments(app, event); %#ok<ASGLU>
            reconstructed_img = SheppLogan(handles.old_input, app.phantom_size.Value, app.n_transducers.Value, app.angular_inc.Value, app.probe_type.Value); %#ok<ST2NM> 
            handles.img_for_analysis = reconstructed_img;
            axes(handles.axes_new); hold on;
            app.axes_new.Title.String = "Reconstructed Phantom";
            imshow(reconstructed_img);
            set(app.start_analyze, 'Enable', 'on');
            guidata(hObject, handles);
        end

        % Button pushed function: start_analyze
        function analyze_image(app, event)
             % Create GUIDE-style callback args - Added by Migration Tool
            [hObject, eventdata, handles] = convertToGUIDECallbackArguments(app, event); %#ok<ASGLU>
            set(app.SI_Axes, 'Visible', 'off');
            delete(handles.original_line);
            delete(handles.new_line);
            delete(handles.legend);
            delete(handles.image_diff);
            %app.figure1.Position = [520 345 760 580];
            set(app.figure1, "Position", [520 345 760 580])
            handles.ImageSubt = handles.img_for_analysis - handles.old_input;
            app.difference_display.Title.String = "Reconstructed - Original Generated Phantom";
            handles.image_diff = imshow(handles.ImageSubt, "Parent", app.difference_display);
            set(hObject, 'Enable', 'off');
            set(app.ResultsPanel, 'Visible', 'on');
            set(app.NextButton, 'Tooltip', 'Signal Intensity by Row')
            handles.next_plot = 1;
            guidata(hObject, handles);
        end

        % Button pushed function: NextButton
        function next(app, event)
            [hObject, eventdata, handles] = convertToGUIDECallbackArguments(app, event); %#ok<ASGLU>
            if handles.next_plot == 0
                delete(handles.original_line);
                delete(handles.new_line);
                delete(handles.legend);
                handles.next_plot = 1;
                app.difference_display.Title.String = "Reconstructed - Original Generated Phantom";
                set(app.SI_Axes, 'Visible', 'off');
                handles.image_diff = imshow(handles.ImageSubt, "Parent", app.difference_display);
                set(app.NextButton, 'Tooltip', 'Signal Intensity by Row')
            elseif handles.next_plot == 1
                handles.next_plot = 2;
                set(app.SI_Axes, 'Visible', 'on');
                set(app.NextButton, 'Tooltip', 'Signal Intensity by Column')
                delete(handles.image_diff);
                app.difference_display.Title.String = "";
                axes(app.SI_Axes); hold on;
                handles.original_line = plot(handles.old_input(handles.rAlign,:)); 
                handles.new_line = plot(handles.img_for_analysis(handles.rAlign,:));
                colororder(handles.newcolors);
                title('Signal Intensity by Row');
                handles.legend = legend('Phantom','Reconstruction');
                hold off;
            elseif handles.next_plot == 2
                set(app.NextButton, 'Tooltip', 'Reconstructed Image - Original Generated Phantom')
                axes(app.SI_Axes); hold on;
                handles.next_plot = 0;
                delete(handles.original_line)
                delete(handles.new_line)
                handles.original_line = plot(handles.old_input(handles.cAlign,:)); 
                handles.new_line = plot(handles.img_for_analysis(handles.cAlign,:));
                colororder(handles.newcolors);
                title('Signal Intensity by Column');
                handles.legend = legend('Phantom','Reconstruction');
                hold off;
            end
            guidata(hObject, handles);
        end

        % Value changed function: phantom_size
        function enable_gen(app, event)
            set(app.get_phantom, 'Enable', 'on');
            set(app.ResultsPanel, 'Visible', 'off');
            set(app.reconstruct, 'Enable', 'off');
            set(app.start_analyze, 'Enable', 'off');
            set(app.figure1, "Position", [520 345 380 580]);
        end
    end

